use futures::future::join_all;

async fn process_multiple() -> Vec<Result<String, Error>> {
    let tasks: Vec<_> = (0..10)
        .map(|i| {
            task::spawn(async move {
                // Async work
                fetch_item(i).await
            })
        })
        .collect();

    // Wait for all tasks
