package main

import (
    "context"
    "fmt"
    "net/http"
    "os"
    "os/signal"
    "syscall"
    "time"

    "github.com/gin-gonic/gin"
    "github.com/prometheus/client_golang/prometheus/promhttp"
    "go.opentelemetry.io/otel"
    "go.opentelemetry.io/otel/exporters/jaeger"
    "go.uber.org/zap"
)

type Server struct {
    router *gin.Engine
    logger *zap.Logger
    config *Config
}

func NewServer(cfg *Config, logger *zap.Logger) *Server {
    router := gin.New()
    router.Use(gin.Recovery())
    router.Use(LoggingMiddleware(logger))
    router.Use(TracingMiddleware())

    return &Server{
        router: router,
        logger: logger,
        config: cfg,
    }
}

func (s *Server) SetupRoutes() {
    // Health checks
    s.router.GET("/health", s.healthCheck)
    s.router.GET("/ready", s.readinessCheck)

    // Metrics
    s.router.GET("/metrics", gin.WrapH(promhttp.Handler()))

    // API routes
    v1 := s.router.Group("/api/v1")
    {
        v1.GET("/users", s.getUsers)
        v1.POST("/users", s.createUser)
        v1.GET("/users/:id", s.getUser)
    }
}

func (s *Server) healthCheck(c *gin.Context) {
    c.JSON(http.StatusOK, gin.H{"status": "healthy"})
}

func (s *Server) Start(ctx context.Context) error {
    srv := &http.Server{
        Addr:    fmt.Sprintf(":%d", s.config.Port),
        Handler: s.router,
    }

    // Graceful shutdown
    go func() {
        <-ctx.Done()
        s.logger.Info("Shutting down server...")

        shutdownCtx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
        defer cancel()

        if err := srv.Shutdown(shutdownCtx); err != nil {
            s.logger.Error("Server forced to shutdown", zap.Error(err))
        }
    }()

    s.logger.Info("Starting server", zap.Int("port", s.config.Port))
    return srv.ListenAndServe()
}

func main() {
    // Initialize logger
    logger, _ := zap.NewProduction()
    defer logger.Sync()

    // Load configuration
    cfg := LoadConfig()

    // Initialize tracing
    if err := initTracing(cfg.ServiceName); err != nil {
        logger.Fatal("Failed to initialize tracing", zap.Error(err))
    }

    // Create server
    server := NewServer(cfg, logger)
    server.SetupRoutes()

    // Context for graceful shutdown
    ctx, cancel := context.WithCancel(context.Background())
    defer cancel()

    // Handle signals
    sigChan := make(chan os.Signal, 1)
    signal.Notify(sigChan, os.Interrupt, syscall.SIGTERM)

    go func() {
        <-sigChan
        logger.Info("Received shutdown signal")
        cancel()
    }()

    // Start server
    if err := server.Start(ctx); err != nil && err != http.ErrServerClosed {
        logger.Fatal("Server failed", zap.Error(err))
    }
}

func initTracing(serviceName string) error {
    exporter, err := jaeger.New(jaeger.WithCollectorEndpoint())
    if err != nil {
        return err
    }

    tp := tracesdk.NewTracerProvider(
        tracesdk.WithBatcher(exporter),
        tracesdk.WithResource(resource.NewWithAttributes(
            semconv.SchemaURL,
            semconv.ServiceNameKey.String(serviceName),
        )),
    )

    otel.SetTracerProvider(tp)
    return nil
}
```

### Repository Pattern (Go)
```go
package repository

import (
    "context"
    "database/sql"
    "fmt"

    "github.com/jmoiron/sqlx"
)

type UserRepository interface {
    GetByID(ctx context.Context, id int64) (*User, error)
    Create(ctx context.Context, user *User) error
    List(ctx context.Context, limit, offset int) ([]*User, error)
}

type postgresUserRepository struct {
    db *sqlx.DB
}

func NewUserRepository(db *sqlx.DB) UserRepository {
    return &postgresUserRepository{db: db}
}

func (r *postgresUserRepository) GetByID(ctx context.Context, id int64) (*User, error) {
    var user User
    query := `SELECT id, email, name, created_at FROM users WHERE id = $1`

    if err := r.db.GetContext(ctx, &user, query, id); err != nil {
        if err == sql.ErrNoRows {
            return nil, fmt.Errorf("user not found")
        }
        return nil, err
    }

    return &user, nil
}

func (r *postgresUserRepository) Create(ctx context.Context, user *User) error {
    query := `
        INSERT INTO users (email, name, created_at)
        VALUES ($1, $2, $3)
        RETURNING id`

    return r.db.QueryRowContext(ctx, query, user.Email, user.Name, time.Now()).
        Scan(&user.ID)
}
