### Composables (Reusable Logic)
```typescript
// composables/useAsync.ts
import { ref, Ref } from 'vue'

interface UseAsyncReturn<T> {
  data: Ref<T | null>
  error: Ref<Error | null>
  isLoading: Ref<boolean>
  execute: () => Promise<void>
}

export function useAsync<T>(
  asyncFunction: () => Promise<T>
): UseAsyncReturn<T> {
  const data = ref<T | null>(null)
  const error = ref<Error | null>(null)
  const isLoading = ref(false)

  const execute = async () => {
    isLoading.value = true
    error.value = null

    try {
      data.value = await asyncFunction()
    } catch (e) {
      error.value = e as Error
    } finally {
      isLoading.value = false
    }
  }

  return { data, error, isLoading, execute }
}

// Usage in component
<script setup lang="ts">
import { useAsync } from '@/composables/useAsync'

const { data: users, isLoading, error, execute } = useAsync(async () => {
  const response = await fetch('/api/users')
  return response.json()
})

onMounted(() => {
  execute()
})
</script>
```

### Form Handling with Validation
```typescript
// composables/useForm.ts
import { reactive, computed } from 'vue'

interface ValidationRule<T> {
  validate: (value: T) => boolean
  message: string
}

interface FieldConfig<T> {
  value: T
  rules?: ValidationRule<T>[]
}

export function useForm<T extends Record<string, any>>(
  config: Record<keyof T, FieldConfig<T[keyof T]>>
) {
  const form = reactive<T>({} as T)
  const errors = reactive<Partial<Record<keyof T, string>>>({})
  const touched = reactive<Partial<Record<keyof T, boolean>>>({})

  // Initialize form values
  Object.keys(config).forEach((key) => {
    form[key as keyof T] = config[key].value
  })

  const validateField = (field: keyof T): boolean => {
    const rules = config[field].rules || []
    const value = form[field]

    for (const rule of rules) {
      if (!rule.validate(value)) {
        errors[field] = rule.message
        return false
      }
    }

    delete errors[field]
    return true
  }

  const validateAll = (): boolean => {
    let isValid = true
    Object.keys(config).forEach((key) => {
      if (!validateField(key as keyof T)) {
        isValid = false
      }
    })
    return isValid
  }

  const isValid = computed(() => Object.keys(errors).length === 0)

  return {
    form,
    errors,
    touched,
    validateField,
    validateAll,
    isValid,
  }
}

// Usage
<script setup lang="ts">
interface LoginForm {
  email: string
  password: string
}

const { form, errors, validateAll, isValid } = useForm<LoginForm>({
  email: {
    value: '',
    rules: [
      {
        validate: (v) => !!v,
        message: 'Email is required'
      },
      {
        validate: (v) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(v),
        message: 'Invalid email format'
      }
    ]
  },
  password: {
    value: '',
    rules: [
      {
        validate: (v) => v.length >= 8,
        message: 'Password must be at least 8 characters'
      }
    ]
  }
})

const handleSubmit = () => {
  if (validateAll()) {
    console.log('Form is valid:', form)
  }
}
</script>
```

