## Concurrency Patterns

### Goroutines and Channels

```go
package main

import (
    "context"
    "fmt"
    "sync"
    "time"
)

// Worker pool pattern
func ProcessItems(items []Item) []Result {
    numWorkers := 10
    itemChan := make(chan Item, len(items))
    resultChan := make(chan Result, len(items))

    // Start workers
    var wg sync.WaitGroup
    for i := 0; i < numWorkers; i++ {
        wg.Add(1)
        go worker(&wg, itemChan, resultChan)
    }

    // Send items
    for _, item := range items {
        itemChan <- item
    }
    close(itemChan)

    // Wait for workers and close result channel
    go func() {
        wg.Wait()
        close(resultChan)
    }()

    // Collect results
    results := make([]Result, 0, len(items))
    for result := range resultChan {
        results = append(results, result)
    }

    return results
}

func worker(wg *sync.WaitGroup, items <-chan Item, results chan<- Result) {
    defer wg.Done()
    for item := range items {
        result := processItem(item)
        results <- result
    }
}

// Fan-out, fan-in pattern
func FanOutFanIn(items []Item) []Result {
    numWorkers := 10

    // Fan-out: distribute work
    itemChans := make([]chan Item, numWorkers)
    for i := range itemChans {
        itemChans[i] = make(chan Item)
    }

    // Start workers
    resultChans := make([]<-chan Result, numWorkers)
    for i := 0; i < numWorkers; i++ {
        resultChans[i] = worker(itemChans[i])
    }

    // Distribute items
    go func() {
        for i, item := range items {
            itemChans[i%numWorkers] <- item
        }
        for _, ch := range itemChans {
            close(ch)
        }
    }()

    // Fan-in: merge results
    return merge(resultChans...)
}

func worker(items <-chan Item) <-chan Result {
    results := make(chan Result)
    go func() {
        defer close(results)
        for item := range items {
            results <- processItem(item)
        }
    }()
    return results
}

func merge(channels ...<-chan Result) []Result {
    var wg sync.WaitGroup
    out := make(chan Result)

    // Start a goroutine for each input channel
    for _, c := range channels {
        wg.Add(1)
        go func(ch <-chan Result) {
            defer wg.Done()
            for result := range ch {
                out <- result
            }
        }(c)
    }

    // Close out channel when all inputs are done
    go func() {
        wg.Wait()
        close(out)
    }()

    // Collect results
    var results []Result
    for result := range out {
        results = append(results, result)
    }
    return results
}

// Pipeline pattern
func Pipeline(items []int) <-chan int {
    // Stage 1: Generate
    in := generate(items)

    // Stage 2: Square
    squared := square(in)

    // Stage 3: Filter
    filtered := filter(squared, func(n int) bool {
        return n%2 == 0
    })

    return filtered
}

func generate(items []int) <-chan int {
    out := make(chan int)
    go func() {
        defer close(out)
        for _, item := range items {
            out <- item
        }
    }()
    return out
}

func square(in <-chan int) <-chan int {
    out := make(chan int)
    go func() {
        defer close(out)
        for n := range in {
            out <- n * n
        }
    }()
    return out
}

func filter(in <-chan int, predicate func(int) bool) <-chan int {
    out := make(chan int)
    go func() {
        defer close(out)
        for n := range in {
            if predicate(n) {
                out <- n
            }
        }
    }()
    return out
}
```

### Context Usage

```go
package main

import (
    "context"
    "fmt"
    "time"
)

// Pass context as first parameter
func FetchUser(ctx context.Context, userID string) (*User, error) {
    // Create a timeout context
    ctx, cancel := context.WithTimeout(ctx, 5*time.Second)
    defer cancel()

    // Use context in HTTP request
    req, err := http.NewRequestWithContext(ctx, "GET", "/users/"+userID, nil)
    if err != nil {
        return nil, err
    }

    resp, err := http.DefaultClient.Do(req)
    if err != nil {
        return nil, err
    }
    defer resp.Body.Close()

    // Process response...
    return user, nil
}

// Propagate cancellation
func ProcessBatch(ctx context.Context, items []Item) error {
    for _, item := range items {
        select {
        case <-ctx.Done():
            return ctx.Err()
        default:
            if err := ProcessItem(ctx, item); err != nil {
                return err
            }
        }
    }
    return nil
}

// Context with values (use sparingly)
type contextKey string

const userIDKey contextKey = "userID"

func WithUserID(ctx context.Context, userID string) context.Context {
    return context.WithValue(ctx, userIDKey, userID)
}

func GetUserID(ctx context.Context) (string, bool) {
    userID, ok := ctx.Value(userIDKey).(string)
    return userID, ok
}
