## State Management with Pinia

```typescript
// stores/user.ts
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'

interface User {
  id: string
  name: string
  email: string
}

export const useUserStore = defineStore('user', () => {
  // State
  const currentUser = ref<User | null>(null)
  const users = ref<User[]>([])
  const loading = ref(false)
  const error = ref<Error | null>(null)

  // Getters (computed)
  const isAuthenticated = computed(() => currentUser.value !== null)
  const userCount = computed(() => users.value.length)
  const getUserById = computed(() => {
    return (id: string) => users.value.find(u => u.id === id)
  })

  // Actions
  async function fetchUsers() {
    loading.value = true
    error.value = null

    try {
      const response = await fetch('/api/users')
      users.value = await response.json()
    } catch (e) {
      error.value = e as Error
    } finally {
      loading.value = false
    }
  }

  async function login(email: string, password: string) {
    loading.value = true
    error.value = null

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      })

      if (!response.ok) {
        throw new Error('Login failed')
      }

      currentUser.value = await response.json()
    } catch (e) {
      error.value = e as Error
      throw e
    } finally {
      loading.value = false
    }
  }

  function logout() {
    currentUser.value = null
  }

  async function updateUser(userId: string, updates: Partial<User>) {
    const index = users.value.findIndex(u => u.id === userId)
    if (index !== -1) {
      users.value[index] = { ...users.value[index], ...updates }
    }

    // Persist to backend
    await fetch(`/api/users/${userId}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updates)
    })
  }

  return {
    // State
    currentUser,
    users,
    loading,
    error,
    // Getters
    isAuthenticated,
    userCount,
    getUserById,
    // Actions
    fetchUsers,
    login,
    logout,
    updateUser
  }
})

// Using the store in components
<script setup lang="ts">
import { useUserStore } from '@/stores/user'
import { storeToRefs } from 'pinia'

const userStore = useUserStore()

// Get reactive refs from store (maintains reactivity)
const { currentUser, users, loading, isAuthenticated } = storeToRefs(userStore)

// Actions are used directly (don't need storeToRefs)
const { login, logout, fetchUsers } = userStore

onMounted(() => {
  if (isAuthenticated.value) {
    fetchUsers()
  }
})
</script>
```

