```typescript
// packages/cli/src/commands/watch.ts
import { Command } from 'commander';
import express from 'express';
import { Webhooks } from '@octokit/webhooks';
import localtunnel from 'localtunnel';

export const watchCommand = new Command('watch')
  .description('üå∏ WatchÁ∂ôÁ∂öÁõ£Ë¶ñ„É¢„Éº„ÉâÔºàWebhookÂûãÔºâ')
  .option('--events <events...>', 'Áõ£Ë¶ñ„Ç§„Éô„É≥„Éà', ['issues', 'pull_request', 'push'])
  .option('--notify <channels...>', 'ÈÄöÁü•„ÉÅ„É£„É≥„Éç„É´', ['console'])
  .option('--port <port>', 'Webhook„Çµ„Éº„Éê„Éº„Éù„Éº„Éà', '3939')
  .action(async (options) => {
    const app = express();
    const webhooks = new Webhooks({
      secret: process.env.GITHUB_WEBHOOK_SECRET || 'miyabi-secret'
    });

    // WebhookÂèó‰ø°„Ç®„É≥„Éâ„Éù„Ç§„É≥„Éà
    app.post('/webhook', express.json(), async (req, res) => {
      try {
        await webhooks.verifyAndReceive({
          id: req.headers['x-github-delivery'] as string,
          name: req.headers['x-github-event'] as any,
          signature: req.headers['x-hub-signature-256'] as string,
          payload: req.body
        });
        res.status(200).send('OK');
      } catch (error) {
        res.status(400).send('Invalid signature');
      }
    });

    // Issues„Ç§„Éô„É≥„ÉàÂá¶ÁêÜ
    webhooks.on('issues.opened', async ({ payload }) => {
      const { issue } = payload;
      console.log(`üÜï Issue opened: #${issue.number} - ${issue.title}`);

      // „É©„Éô„É´Ëß£Êûê
      const priorityLabel = issue.labels.find(l => l.name.includes('priority:'));
      const isPriority = priorityLabel?.name.includes('P0') || priorityLabel?.name.includes('P1');

      // ÈÄöÁü•ÈÄÅ‰ø°
      if (isPriority) {
        await sendNotification(options.notify, {
          title: `üî• [${priorityLabel.name}] New Issue`,
          body: `#${issue.number}: ${issue.title}`,
          url: issue.html_url
        });
      }

      // „ÉÄ„ÉÉ„Ç∑„É•„Éú„Éº„ÉâÊõ¥Êñ∞
      await updateDashboard();
    });

    // „Çµ„Éº„Éê„ÉºËµ∑Âãï
    const server = app.listen(options.port, () => {
      console.log(`üå∏ Miyabi Watch Mode started on port ${options.port}`);
    });

    // localtunnelËµ∑ÂãïÔºàÂ§ñÈÉ®„Ç¢„ÇØ„Çª„ÇπÁî®Ôºâ
    const tunnel = await localtunnel({ port: options.port });
    console.log(`üåê Webhook URL: ${tunnel.url}/webhook`);
    console.log(`üëâ Register this URL as GitHub Webhook`);

    // Ctrl+C „ÅßÁµÇ‰∫Ü
    process.on('SIGINT', () => {
      console.log('\nüõë Stopping Watch Mode...');
      server.close();
      tunnel.close();
      process.exit(0);
    });
  });
```

