  BLINK_4: 14,
  BLINK_2: 15,
} as const;

type ChannelName = keyof typeof Channels;

// Button states
type ButtonState = 'off' | 'on' | 'blink';

// Note mappings
const Notes = {
  PAD_MIN: 0,
  PAD_MAX: 63,
  TRACK_MIN: 100,
  TRACK_MAX: 107,
  SCENE_MIN: 112,
  SCENE_MAX: 119,
  SHIFT: 122,
} as const;

export { Colors, Channels, Notes, ColorName, ChannelName, ButtonState };
```

