{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "lean4-tools",
  "description": "Lean 4 theorem prover support for Claude Code",
  "version": "1.0.0",
  "owner": { "name": "Alok Singh" },
  "plugins": [{
    "name": "lean4",
    "description": "Lean 4 language server for theorem proving",
    "version": "1.0.0",
    "author": { "name": "Alok Singh" },
    "source": "./plugins/lean4",
    "category": "development",
    "strict": false,
    "lspServers": {
      "lean": {
        "command": "lake",
        "args": ["serve", "--"],
        "extensionToLanguage": {
          ".lean": "lean"
        }
      }
    }
  }]
}
