files..claude-plugin/plugin.json:
{
  "name": "context-daddy",
  "version": "0.10.13",
  "description": "Your codebase's context needs a responsible adult. Fast code exploration, living project narratives, and tribal knowledge that survives across sessions.",
  "author": {
    "name": "Robert Taylor"
  },
  "keywords": ["context", "narrative", "repo-map", "tribal-knowledge", "documentation"],
  "repository": "https://github.com/chipflow/context-daddy",
  "mcpServers": {
    "repo-map": {
      "command": "uv",
      "args": ["run", "${CLAUDE_PLUGIN_ROOT}/servers/repo-map-server.py"],
      "env": {
        "PROJECT_ROOT": "${PWD}"
      }
    }
  }
}
