```csharp
/**
 * Input: Microsoft.AspNetCore.Http, System.Threading.Tasks, Serilog
 * Output: RequestLoggingMiddleware ç±», InvokeAsync() æ–¹æ³•
 * Pos: ä¸­é—´ä»¶å±‚-è¯·æ±‚æ—¥å¿—ä¸­é—´ä»¶ï¼Œè®°å½• HTTP è¯·æ±‚è¯¦æƒ…
 *
 * æœ¬æ³¨é‡Šåœ¨æ–‡ä»¶ä¿®æ”¹æ—¶è‡ªåŠ¨æ›´æ–°
 */

using Microsoft.AspNetCore.Http;
using System;
using System.Diagnostics;
using System.IO;
using System.Text;
using System.Threading.Tasks;
using Serilog;

namespace MyApp.Middleware
{
    /// <summary>
    /// è¯·æ±‚æ—¥å¿—ä¸­é—´ä»¶
    /// </summary>
    public class RequestLoggingMiddleware
    {
        private readonly RequestDelegate _next;
        private readonly ILogger _logger;

        public RequestLoggingMiddleware(RequestDelegate next, ILogger logger)
        {
            _next = next;
            _logger = logger;
        }

        public async Task InvokeAsync(HttpContext context)
        {
            var stopwatch = Stopwatch.StartNew();

            // è®°å½•è¯·æ±‚
            await LogRequest(context);

            // ä¿å­˜åŸå§‹å“åº”æµ
            var originalBodyStream = context.Response.Body;

            try
            {
                using var responseBody = new MemoryStream();
                context.Response.Body = responseBody;

                // æ‰§è¡Œä¸‹ä¸€ä¸ªä¸­é—´ä»¶
                await _next(context);

                stopwatch.Stop();

                // è®°å½•å“åº”
                await LogResponse(context, stopwatch.ElapsedMilliseconds);

                // å¤åˆ¶å“åº”åˆ°åŸå§‹æµ
                await responseBody.CopyToAsync(originalBodyStream);
            }
            finally
            {
                context.Response.Body = originalBodyStream;
            }
        }

        private async Task LogRequest(HttpContext context)
        {
            context.Request.EnableBuffering();

            var body = await ReadRequestBody(context.Request);

            _logger.Information(
                "HTTP {Method} {Path} {QueryString} - Body: {Body}",
                context.Request.Method,
                context.Request.Path,
                context.Request.QueryString,
                body
            );

            context.Request.Body.Position = 0;
        }

        private async Task LogResponse(HttpContext context, long elapsedMs)
        {
            context.Response.Body.Seek(0, SeekOrigin.Begin);
            var body = await new StreamReader(context.Response.Body).ReadToEndAsync();
            context.Response.Body.Seek(0, SeekOrigin.Begin);

            _logger.Information(
                "HTTP {Method} {Path} responded {StatusCode} in {ElapsedMs}ms - Body: {Body}",
                context.Request.Method,
                context.Request.Path,
                context.Response.StatusCode,
                elapsedMs,
                body
            );
        }

        private async Task<string> ReadRequestBody(HttpRequest request)
        {
            using var reader = new StreamReader(
                request.Body,
                encoding: Encoding.UTF8,
                detectEncodingFromByteOrderMarks: false,
                leaveOpen: true
            );

            return await reader.ReadToEndAsync();
        }
    }
}
