## ViewModel ç¤ºä¾‹ (MVVM)

```swift
/**
 Input: Foundation, Combine, UserService, User æ¨¡å‹
 Output: UserViewModel ç±», @Published properties, async methods
 Pos: ä¸šåŠ¡å±‚-ç”¨æˆ·è§†å›¾æ¨¡å‹ï¼Œç®¡ç†ç”¨æˆ·æ•°æ®å’Œä¸šåŠ¡é€»è¾‘

 æœ¬æ³¨é‡Šåœ¨æ–‡ä»¶ä¿®æ”¹æ—¶è‡ªåŠ¨æ›´æ–°
 */

import Foundation
import Combine

@MainActor
class UserViewModel: ObservableObject {
    @Published var users: [User] = []
    @Published var isLoading = false
    @Published var errorMessage: String?

    private let userService: UserService
    private var cancellables = Set<AnyCancellable>()

    init(userService: UserService = UserService()) {
        self.userService = userService
    }

    /// åŠ è½½ç”¨æˆ·åˆ—è¡¨
    func loadUsers() async {
        isLoading = true
        errorMessage = nil

        do {
            let fetchedUsers = try await userService.fetchUsers()
                .async()
            users = fetchedUsers
        } catch {
            errorMessage = error.localizedDescription
        }

        isLoading = false
    }

    /// åˆ·æ–°ç”¨æˆ·åˆ—è¡¨
    func refresh() async {
        await loadUsers()
    }

    /// åˆ›å»ºæ–°ç”¨æˆ·
    /// - Parameter request: åˆ›å»ºç”¨æˆ·è¯·æ±‚
    func createUser(_ request: CreateUserRequest) async throws {
        let newUser = try await userService.createUser(request).async()
        users.append(newUser)
    }

    /// åˆ é™¤ç”¨æˆ·
    /// - Parameter offsets: ç´¢å¼•é›†åˆ
    func deleteUsers(at offsets: IndexSet) async {
        let usersToDelete = offsets.map { users[$0] }

        for user in usersToDelete {
            do {
                try await userService.deleteUser(id: user.id).async()
                users.removeAll { $0.id == user.id }
            } catch {
                errorMessage = "Failed to delete user: \(error.localizedDescription)"
            }
        }
    }
}

// Combine to async/await helper
extension Publisher {
    func async() async throws -> Output {
        try await withCheckedThrowingContinuation { continuation in
            var cancellable: AnyCancellable?

            cancellable = first()
                .sink { completion in
                    switch completion {
                    case .finished:
                        break
                    case .failure(let error):
                        continuation.resume(throwing: error)
                    }
                    cancellable?.cancel()
                } receiveValue: { value in
                    continuation.resume(returning: value)
                }
        }
    }
}
