            <ActivityIndicator
              color={variant === 'secondary' ? '#374151' : '#ffffff'}
              style={styles.spinner}
            />
          )}
          <Text style={[styles.text, styles[`${variant}Text`]]}>
            {children}
          </Text>
        </Pressable>
      );
    }

    const styles = StyleSheet.create({
      base: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
