        paddingVertical: 10,
      },
      lg: {
        paddingHorizontal: 24,
        paddingVertical: 14,
      },
      disabled: {
        opacity: 0.5,
      },
      pressed: {
        opacity: 0.8,
      },
      spinner: {
        marginRight: 8,
      },
      text: {
        fontWeight: '600',
        textAlign: 'center',
      },
      primaryText: {
        color: '#ffffff',
      },
      secondaryText: {
        color: '#1f2937',
      },
      dangerText: {
        color: '#ffffff',
      },
