}

// Prefetch after initial load
function usePrefetchRoutes(): void {
  useEffect(() => {
    const timer = setTimeout(() => {
      import('./pages/Dashboard');
      import('./pages/Settings');
    }, 2000); // Prefetch after 2s

    return () => clearTimeout(timer);
  }, []);
