{
  "name": "cpt",
  "version": "0.1.0",
  "description": "Claude Plan Tracker - Persist and track implementation plans across Claude Code sessions",
  "author": {
    "name": "jansmrcka"
  },
  "homepage": "https://github.com/jansmrcka/cpt",
  "repository": "https://github.com/jansmrcka/cpt",
  "license": "MIT",
  "hooks": {
    "SessionStart": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/cpt hook session-start"
          }
        ]
      }
    ],
    "SessionEnd": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/cpt hook session-end"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/cpt hook post-tool-use"
          }
        ]
      }
    ]
  }
}
