{
  "name": "strawberry-terminal",
  "version": "1.0.1",
  "description": "Strawberry Terminal integration - VM orchestration, browser automation, and activity tracking for the Strawberry terminal app",
  "author": {
    "name": "Liam Fuller",
    "url": "https://github.com/LiamFuller07"
  },
  "repository": "https://github.com/LiamFuller07/strawberry-terminal-plugin",
  "keywords": ["strawberry", "terminal", "vm", "browser", "automation", "trycua", "computer-use"]
}

{
  "description": "Strawberry Terminal activity tracking - reports all Claude activity to the Strawberry UI",
  "hooks": {
    "PreToolUse": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/hooks/track-activity.sh pre",
            "timeout": 5
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/hooks/track-activity.sh post",
            "timeout": 5
          }
        ]
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/hooks/track-activity.sh stop",
            "timeout": 5
          }
        ]
      }
    ],
    "SessionStart": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/hooks/track-activity.sh session-start",
            "timeout": 5
          }
        ]
      }
    ],
    "UserPromptSubmit": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/hooks/track-activity.sh prompt",
            "timeout": 5
          }
        ]
      }
    ]
  }
}
