  "description": "Task validation hooks for lovely-tasks plugin",
  "hooks": {
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "bun run ${CLAUDE_PLUGIN_ROOT}/scripts/hook-stop.ts",
            "timeout": 60
          }
        ]
      }
    ]
  }
}

