{
	"$schema": "../schemas/marketplace.schema.json",
	"name": "mearman",
	"owner": {
		"name": "Mearman"
	},
	"metadata": {
		"description": "A collection of Claude Code plugins and skills"
	},
	"plugins": [
		{
			"name": "wayback",
			"source": "./plugins/wayback",
			"description": "Wayback Machine Archive: Tools for checking, submitting, listing, screenshotting, and cache management for archived URLs",
			"version": "0.10.1"
		},
		{
			"name": "npm-registry",
			"source": "./plugins/npm-registry",
			"description": "npm Registry: Tools for searching packages, getting metadata, checking existence, and fetching download statistics",
			"version": "0.2.0"
		},
		{
			"name": "pypi-json",
			"source": "./plugins/pypi-json",
			"description": "PyPI JSON API: Tools for querying Python package metadata, release information, and statistics from the Python Package Index",
			"version": "0.2.3"
		},
		{
			"name": "gravatar",
			"source": "./plugins/gravatar",
			"description": "Gravatar Avatar URLs: Tools for generating avatar URLs from email addresses",
			"version": "0.2.1"
		},
		{
			"name": "github-api",
			"source": "./plugins/github-api",
			"description": "GitHub REST API: Tools for repository metadata, READMEs, user profiles, and rate limit checking",
			"version": "0.2.0"
		},
		{
			"name": "npms-io",
			"source": "./plugins/npms-io",
			"description": "NPMS Package Analysis: Tools for package quality analysis, comparison, and name suggestions",
			"version": "0.2.0"
		},
		{
			"name": "cve-search",
			"source": "./plugins/cve-search",
			"description": "CVE Search: Tools for searching vulnerabilities by CVE ID, product name, or vendor, with detailed vulnerability information and dependency auditing",
			"version": "0.3.0"
		},
		{
			"name": "bib",
			"source": "./plugins/bib",
			"description": "Bibliography manipulation plugin supporting BibTeX, BibLaTeX, RIS, EndNote XML, and CSL JSON formats. Convert, validate, merge, filter, and perform CRUD operations on bibliography files.",
			"version": "0.2.1"
		},
		{
			"name": "tex",
			"source": "./plugins/tex",
			"description": "LaTeX manipulation, generation, and conversion tools",
			"version": "0.2.1"
		},
		{
			"name": "json-schema",
			"source": "./plugins/json-schema",
			"description": "JSON Schema Validation: Tools for validating JSON schemas themselves, validating JSON files against schemas, and auto-validating files against their $schema reference",
			"version": "0.2.0"
		},
		{
			"name": "json-lsp",
			"source": "./plugins/json-lsp",
			"description": "JSON Language Server: Provides validation, completion, hover, document symbols, and formatting for JSON and JSONC files with automatic schema association support",
			"version": "0.2.0"
		}
	]
}

