{
  "name": "flutter-claude-code",
  "version": "1.0.0",
  "description": "Flutter Claude Code setup with 15 productivity commands and 17 specialized AI agents for Flutter development with Supabase, Riverpod, and Drift",
  "author": "CodeWithNabi",
  "license": "MIT",
  "commands": [
    {
      "name": "new-task",
      "path": ".claude/commands/new-task.md",
      "description": "Analyze task complexity and create Flutter implementation plan"
    },
    {
      "name": "code-explain",
      "path": ".claude/commands/misc/code-explain.md",
      "description": "Generate detailed code explanations"
    },
    {
      "name": "code-optimize",
      "path": ".claude/commands/misc/code-optimize.md",
      "description": "Optimize Flutter code for performance"
    },
    {
      "name": "code-cleanup",
      "path": ".claude/commands/misc/code-cleanup.md",
      "description": "Clean up and refactor Dart/Flutter code"
    },
    {
      "name": "feature-plan",
      "path": ".claude/commands/misc/feature-plan.md",
      "description": "Plan Flutter feature with CodeWithNabi architecture"
    },
    {
      "name": "lint",
      "path": ".claude/commands/misc/lint.md",
      "description": "Run Dart analyzer and fix issues"
    },
    {
      "name": "docs-generate",
      "path": ".claude/commands/misc/docs-generate.md",
      "description": "Generate DartDoc documentation"
    },
    {
      "name": "service-new",
      "path": ".claude/commands/api/api-new.md",
      "description": "Create new service/repository layer with all 4 architecture layers"
    },
    {
      "name": "service-test",
      "path": ".claude/commands/api/api-test.md",
      "description": "Test services and repositories with mocktail"
    },
    {
      "name": "rls-protect",
      "path": ".claude/commands/api/api-protect.md",
      "description": "Add Supabase RLS policies and secure storage"
    },
    {
      "name": "widget-new",
      "path": ".claude/commands/ui/component-new.md",
      "description": "Create new Flutter widget (Stateless, Stateful, Consumer)"
    },
    {
      "name": "screen-new",
      "path": ".claude/commands/ui/page-new.md",
      "description": "Create new Flutter screen with go_router and Riverpod"
    },
    {
      "name": "types-gen",
      "path": ".claude/commands/supabase/types-gen.md",
      "description": "Generate Dart entities and Drift tables from Supabase schema"
    },
    {
      "name": "edge-function-new",
      "path": ".claude/commands/supabase/edge-function-new.md",
      "description": "Create new Supabase Edge Function"
    },
    {
      "name": "schema-new",
      "path": ".claude/commands/supabase/schema-new.md",
      "description": "Generate Supabase PostgreSQL schema with RLS policies, triggers, and enums"
    }
  ],
  "agents": [
    {
      "name": "tech-stack-researcher",
      "path": ".claude/agents/tech-stack-researcher.md",
      "description": "Research and recommend technology choices for feature development"
    },
    {
      "name": "backend-architect",
      "path": ".claude/agents/backend-architect.md",
      "description": "Design reliable backend systems with focus on data integrity, security, and fault tolerance"
    },
    {
      "name": "deep-research-agent",
      "path": ".claude/agents/deep-research-agent.md",
      "description": "Specialist for comprehensive research with adaptive strategies and intelligent exploration"
    },
    {
      "name": "frontend-architect",
      "path": ".claude/agents/frontend-architect.md",
      "description": "Create accessible, performant user interfaces with focus on user experience and modern frameworks"
    },
    {
      "name": "learning-guide",
      "path": ".claude/agents/learning-guide.md",
      "description": "Teach programming concepts and explain code with focus on understanding through progressive learning"
    },
    {
      "name": "performance-engineer",
      "path": ".claude/agents/performance-engineer.md",
      "description": "Optimize system performance through measurement-driven analysis and bottleneck elimination"
    },
    {
      "name": "refactoring-expert",
      "path": ".claude/agents/refactoring-expert.md",
      "description": "Improve code quality and reduce technical debt through systematic refactoring and clean code principles"
    },
    {
      "name": "requirements-analyst",
      "path": ".claude/agents/requirements-analyst.md",
      "description": "Transform ambiguous project ideas into concrete specifications through systematic requirements discovery"
    },
    {
      "name": "security-engineer",
      "path": ".claude/agents/security-engineer.md",
      "description": "Identify security vulnerabilities and ensure compliance with security standards and best practices"
    },
    {
      "name": "system-architect",
      "path": ".claude/agents/system-architect.md",
      "description": "Design scalable system architecture with focus on maintainability and long-term technical decisions"
    },
    {
      "name": "technical-writer",
      "path": ".claude/agents/technical-writer.md",
      "description": "Create clear, comprehensive technical documentation tailored to specific audiences with focus on usability"
    },
    {
      "name": "flutter-architect",
      "path": ".claude/agents/flutter-architect.md",
      "description": "Flutter architecture decisions, folder structure, layer placement, and feature scaffolding using CodeWithNabi Feature-First Clean Architecture"
    },
    {
      "name": "riverpod-expert",
      "path": ".claude/agents/riverpod-expert.md",
      "description": "Riverpod state management, provider patterns, dependency injection wiring, AsyncNotifier patterns, and ref.watch/ref.listen usage"
    },
    {
      "name": "supabase-flutter",
      "path": ".claude/agents/supabase-flutter.md",
      "description": "Supabase integration, offline-first patterns with Drift, sync strategies, and data source implementation for Flutter"
    },
    {
      "name": "testing-expert",
      "path": ".claude/agents/testing-expert.md",
      "description": "Hierarchical Flutter testing with Robot pattern, mocktail, Widget/Integration/Golden tests"
    },
    {
      "name": "error-handling-expert",
      "path": ".claude/agents/error-handling-expert.md",
      "description": "Error handling strategies, custom exceptions, error logging, AsyncError handling, and global error boundaries"
    },
    {
      "name": "monetization-expert",
      "path": ".claude/agents/monetization-expert.md",
      "description": "In-app purchases, RevenueCat integration, subscription logic, paywalls, and premium feature gating"
    }
  ],
  "mcpServers": {
    "context7": {
      "command": "npx",
      "args": ["-y", "@upstash/context7-mcp"],
      "description": "Access up-to-date documentation for any library"
    },
    "playwright": {
      "command": "npx",
      "args": ["@playwright/mcp@latest"],
      "description": "Browser automation and web testing capabilities"
    },
    "supabase": {
      "command": "npx",
      "args": ["-y", "@supabase/mcp-server-supabase"],
      "description": "Supabase database operations and management"
    }
  },
  "tags": [
    "flutter",
    "dart",
    "riverpod",
    "supabase",
    "drift",
    "clean-architecture",
    "offline-first",
    "go-router",
    "mcp",
    "productivity"
  ]
}
