{
  "name": "claudeshack",
  "owner": {
    "name": "Overlord-Z",
    "email": "[email protected]"
  },
  "metadata": {
    "description": "Production-ready skills for Claude Code with autonomous behavior, session health monitoring, and zero-friction intelligence",
    "version": "1.0.0-beta"
  },
  "plugins": [
    {
      "name": "oracle",
      "description": "Project memory and learning system. AUTO-INVOKES to check patterns before changes, record corrections immediately, and prevent user from ever repeating themselves. Integrates with Context7 for current library docs.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/oracle"]
    },
    {
      "name": "guardian",
      "description": "Automatic quality gate and session health monitor. Spawns focused Haiku agents for code review when degradation detected. Validates suggestions against Oracle knowledge. Learns from user feedback to adjust sensitivity.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/guardian"]
    },
    {
      "name": "summoner",
      "description": "Multi-agent orchestration skill for complex tasks requiring coordination, decomposition, and quality control. Use for large implementations, refactoring projects, multi-component features, or any work requiring multiple specialized agents.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/summoner"]
    },
    {
      "name": "wizard",
      "description": "Documentation wizard that intelligently maintains, updates, and generates accurate documentation. Uses Oracle knowledge, searches conversation history, and spawns focused research agents. No hallucinations - only facts with references.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/wizard"]
    },
    {
      "name": "documentation-wizard",
      "description": "Keeps documentation in perfect sync with code and knowledge. Auto-generates README, API docs, ADRs, and onboarding materials. Detects stale documentation and ensures it evolves with the codebase.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/documentation-wizard"]
    },
    {
      "name": "style-master",
      "description": "Expert CSS and frontend styling specialist that analyzes codebases, maintains style guides, suggests improvements, and stays current with modern design patterns. Use when working on frontend styling, creating design systems, or ensuring visual consistency.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/style-master"]
    },
    {
      "name": "evaluator",
      "description": "Skill evaluation and telemetry framework. Collects anonymous usage data and feedback via GitHub Issues and Projects. Privacy-first, opt-in, transparent. Helps improve ClaudeShack skills based on real-world usage.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/evaluator"]
    },
    {
      "name": "smart-init",
      "description": "Interactive ClaudeShack ecosystem initialization. Analyzes codebase, mines history, discusses findings with you, seeds Oracle with verified knowledge, sets up Context7 for current docs. Creates a foundation that improves over use.",
      "source": "./",
      "strict": false,
      "skills": ["./skills/smart-init"]
    }
  ]
}

