{
  "name": "claude-code-buddy",
  "description": "Your friendly AI companion for Claude Code - smart routing, prompt enhancement, and project memory",
  "author": {
    "name": "PCIRCLE AI"
  },
  "version": "2.2.0",
  "homepage": "https://github.com/PCIRCLE-AI/claude-code-buddy",
  "repository": "https://github.com/PCIRCLE-AI/claude-code-buddy",
  "license": "AGPL-3.0",
  "mcpServers": {
    "claude-code-buddy": {
      "type": "stdio",
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/dist/mcp/server-bootstrap.js"],
      "env": {
        "NODE_ENV": "production"
      }
    }
  }
}
