{
  "name": "claude-hub",
  "version": "1.0.0",
  "description": "Personal Claude Code customizations: multi-agent teams, skills, agents, and commands",
  "owner": {
    "name": "Alexander Thompson",
    "github": "AlexanderStephenThompson"
  },
  "plugins": [
    {
      "name": "clean-team",
      "version": "4.1.0",
      "description": "An 8-agent pipeline in two phases. Phase 1: Analyze (organize, format, audit). Phase 2: Refactor (test, plan, challenge, execute, verify). One command — /clean-team:clean — with three modes: full pipeline, audit-only, and resume.",
      "source": "./core/teams/clean-team"
    },
    {
      "name": "implement-team",
      "version": "1.0.0",
      "description": "A 5-agent implementation team: plan → challenge → implement (strict TDD) → security → refactor",
      "source": "./core/teams/implement-team"
    },
    {
      "name": "diagnose-team",
      "version": "1.0.0",
      "description": "A 5-agent diagnostic team for stubborn bugs and implementation mismatches: clarify → investigate → hypothesize → resolve → validate",
      "source": "./core/teams/diagnose-team"
    }
  ]
}
