{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "claude-code-plugins",
  "version": "1.0.0",
  "description": "Bundled plugins for Claude Code including Agent SDK development tools, PR review toolkit, and commit workflows",
  "owner": {
    "name": "Anthropic",
    "email": "support@anthropic.com"
  },
  "plugins": [
    {
      "name": "agent-sdk-dev",
      "description": "Development kit for working with the Claude Agent SDK",
      "source": "./plugins/agent-sdk-dev",
      "category": "development"
    },
    {
      "name": "claude-opus-4-5-migration",
      "description": "Migrate your code and prompts from Sonnet 4.x and Opus 4.1 to Opus 4.5.",
      "version": "1.0.0",
      "author": {
        "name": "William Hu",
        "email": "whu@anthropic.com"
      },
      "source": "./plugins/claude-opus-4-5-migration",
      "category": "development"
    },
    {
      "name": "code-review",
      "description": "Automated code review for pull requests using multiple specialized agents with confidence-based scoring to filter false positives",
      "version": "1.0.0",
      "author": {
        "name": "Boris Cherny",
        "email": "boris@anthropic.com"
      },
      "source": "./plugins/code-review",
      "category": "productivity"
    },
    {
      "name": "commit-commands",
      "description": "Commands for git commit workflows including commit, push, and PR creation",
      "version": "1.0.0",
      "author": {
        "name": "Anthropic",
        "email": "support@anthropic.com"
      },
      "source": "./plugins/commit-commands",
      "category": "productivity"
    },
    {
      "name": "explanatory-output-style",
      "description": "Adds educational insights about implementation choices and codebase patterns (mimics the deprecated Explanatory output style)",
      "version": "1.0.0",
      "author": {
        "name": "Dickson Tsai",
        "email": "dickson@anthropic.com"
      },
      "source": "./plugins/explanatory-output-style",
      "category": "learning"
    },
    {
      "name": "feature-dev",
      "description": "Comprehensive feature development workflow with specialized agents for codebase exploration, architecture design, and quality review",
      "version": "1.0.0",
      "author": {
        "name": "Siddharth Bidasaria",
        "email": "sbidasaria@anthropic.com"
      },
      "source": "./plugins/feature-dev",
      "category": "development"
    },
    {
      "name": "frontend-design",
      "description": "Create distinctive, production-grade frontend interfaces with high design quality. Generates creative, polished code that avoids generic AI aesthetics.",
      "version": "1.0.0",
      "author": {
        "name": "Prithvi Rajasekaran & Alexander Bricken",
        "email": "prithvi@anthropic.com"
      },
      "source": "./plugins/frontend-design",
      "category": "development"
    },
    {
      "name": "hookify",
      "description": "Easily create custom hooks to prevent unwanted behaviors by analyzing conversation patterns or from explicit instructions. Define rules via simple markdown files.",
      "version": "0.1.0",
      "author": {
        "name": "Daisy Hollman",
        "email": "daisy@anthropic.com"
      },
      "source": "./plugins/hookify",
      "category": "productivity"
    },
    {
      "name": "learning-output-style",
      "description": "Interactive learning mode that requests meaningful code contributions at decision points (mimics the unshipped Learning output style)",
      "version": "1.0.0",
      "author": {
        "name": "Boris Cherny",
        "email": "boris@anthropic.com"
      },
      "source": "./plugins/learning-output-style",
      "category": "learning"
    },
    {
      "name": "plugin-dev",
      "description": "Comprehensive toolkit for developing Claude Code plugins. Includes 7 expert skills covering hooks, MCP integration, commands, agents, and best practices. AI-assisted plugin creation and validation.",
      "version": "0.1.0",
      "author": {
        "name": "Daisy Hollman",
        "email": "daisy@anthropic.com"
      },
      "source": "./plugins/plugin-dev",
      "category": "development"
    },
    {
      "name": "pr-review-toolkit",
      "description": "Comprehensive PR review agents specializing in comments, tests, error handling, type design, code quality, and code simplification",
      "version": "1.0.0",
      "author": {
        "name": "Anthropic",
        "email": "support@anthropic.com"
      },
      "source": "./plugins/pr-review-toolkit",
      "category": "productivity"
    },
    {
      "name": "ralph-wiggum",
      "description": "Interactive self-referential AI loops for iterative development. Claude works on the same task repeatedly, seeing its previous work, until completion.",
      "version": "1.0.0",
      "author": {
        "name": "Daisy Hollman",
        "email": "daisy@anthropic.com"
      },
      "source": "./plugins/ralph-wiggum",
      "category": "development"
    },
    {
      "name": "security-guidance",
      "description": "Security reminder hook that warns about potential security issues when editing files, including command injection, XSS, and unsafe code patterns",
      "version": "1.0.0",
      "author": {
        "name": "David Dworken",
        "email": "dworken@anthropic.com"
      },
      "source": "./plugins/security-guidance",
      "category": "security"
    }
  ]
}

{
  "name": "knowledge-work-plugins",
  "owner": {
    "name": "Anthropic"
  },
  "plugins": [
    {
      "name": "productivity",
      "source": "./productivity",
      "description": "Manage tasks, plan your day, and build up memory of important context about your work. Syncs with your calendar, email, and chat to keep everything organized and on track."
    },
    {
      "name": "enterprise-search",
      "source": "./enterprise-search",
      "description": "Search across all of your company's tools in one place. Find anything across email, chat, documents, and wikis without switching between apps."
    },
    {
      "name": "cowork-plugin-management",
      "source": "./cowork-plugin-management",
      "description": "Create, customize, and manage plugins tailored to your organization's tools and workflows. Configure MCP servers, adjust plugin behavior, and adapt templates to match how your team works."
    },
    {
      "name": "sales",
      "source": "./sales",
      "description": "Prospect, craft outreach, and build deal strategy faster. Prep for calls, manage your pipeline, and write personalized messaging that moves deals forward."
    },
    {
      "name": "finance",
      "source": "./finance",
      "description": "Streamline finance and accounting workflows, from journal entries and reconciliation to financial statements and variance analysis. Speed up audit prep, month-end close, and keeping your books clean."
    },
    {
      "name": "data",
      "source": "./data",
      "description": "Write SQL, explore datasets, and generate insights faster. Build visualizations and dashboards, and turn raw data into clear stories for stakeholders."
    },
    {
      "name": "legal",
      "source": "./legal",
      "description": "Speed up contract review, NDA triage, and compliance workflows for in-house legal teams. Draft legal briefs, organize precedent research, and manage institutional knowledge."
    },
    {
      "name": "marketing",
      "source": "./marketing",
      "description": "Create content, plan campaigns, and analyze performance across marketing channels. Maintain brand voice consistency, track competitors, and report on what's working."
    },
    {
      "name": "customer-support",
      "source": "./customer-support",
      "description": "Triage tickets, draft responses, escalate issues, and build your knowledge base. Research customer context and turn resolved issues into self-service content."
    },
    {
      "name": "product-management",
      "source": "./product-management",
      "description": "Write feature specs, plan roadmaps, and synthesize user research faster. Keep stakeholders updated and stay ahead of the competitive landscape."
    },
    {
      "name": "bio-research",
      "source": "./bio-research",
      "description": "Connect to preclinical research tools and databases (literature search, genomics analysis, target prioritization) to accelerate early-stage life sciences R&D"
    }
  ]
}
