{
  "name": "astoreyai-productivity",
  "owner": {
    "name": "Aaron Storey",
    "email": "astoreyai@gmail.com"
  },
  "description": "Professional productivity tools and skills for Claude Code users",
  "version": "1.3.0",
  "plugins": [
    {
      "name": "astoreyai-productivity-skills",
      "description": "Productivity skills including memory-keeper, obsidian-memory-keeper, and todo-keeper with automatic todo persistence for automated context management, daily notes, task tracking, session management, and project status updates with Obsidian vault integration",
      "version": "1.3.0",
      "source": "./",
      "strict": false,
      "author": {
        "name": "Aaron Storey",
        "email": "astoreyai@gmail.com",
        "url": "https://github.com/astoreyai"
      },
      "homepage": "https://github.com/astoreyai/claude-skills",
      "repository": "https://github.com/astoreyai/claude-skills",
      "license": "MIT",
      "keywords": [
        "productivity",
        "memory",
        "context-management",
        "automation",
        "session-tracking",
        "project-management",
        "obsidian",
        "daily-notes",
        "knowledge-base",
        "task-management",
        "todo",
        "todowrite",
        "persistence",
        "hooks"
      ],
      "category": "productivity",
      "skills": [
        "./skills/memory-keeper",
        "./skills/obsidian-memory-keeper",
        "./skills/todo-keeper"
      ],
      "commands": [
        "./commands/update-memory.md",
        "./commands/daily-note.md",
        "./commands/todo.md"
      ],
      "hooks": {
        "SessionStart": [
          {
            "matcher": "*",
            "hooks": [
              {
                "type": "command",
                "command": "python3 ${PLUGIN_DIR}/skills/todo-keeper/load_todos.py",
                "timeout": 10
              }
            ]
          }
        ],
        "SessionEnd": [
          {
            "matcher": "*",
            "hooks": [
              {
                "type": "command",
                "command": "python3 ${PLUGIN_DIR}/skills/memory-keeper/auto_update.py",
                "timeout": 30
              },
              {
                "type": "command",
                "command": "python3 ${PLUGIN_DIR}/skills/todo-keeper/save_todos.py",
                "timeout": 10
              }
            ]
          }
        ]
      }
    }
  ]
}
