      },
      "source": {
        "source": "github",
        "repo": "dazuiba/CCNotify"
      },
      "repository": "https://github.com/dazuiba/CCNotify",
      "license": "MIT",
      "hooks": {
        "UserPromptSubmit": [
          {
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/ccnotify.py UserPromptSubmit"
              }
            ]
          }
        ],
        "Stop": [
          {
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/ccnotify.py Stop"
              }
            ]
          }
        ],
        "Notification": [
          {
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/ccnotify/ccnotify.py Notification"
              }
            ]
          }
        ]
      },
      "description": "CCNotify provides desktop notifications for Claude Code, alerting you when Claude needs your input or completes tasks."
    }
  ]
