{
  "name": "avivsinai-marketplace",
  "owner": {
    "name": "Aviv Sinai"
  },
  "metadata": {
    "description": "Skills and plugins for AI coding assistants",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "amq-cli",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/agent-message-queue.git"
      },
      "description": "Agent Message Queue - atomic Maildir-style message delivery for inter-agent communication with co-op mode for Claude Code and Codex CLI collaboration.",
      "keywords": ["messaging", "agents", "maildir", "queue", "inter-agent", "coordination", "coop"]
    },
    {
      "name": "langfuse",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/langfuse-mcp.git"
      },
      "description": "Langfuse observability - query traces, debug exceptions, analyze sessions, manage prompts and datasets via MCP tools. First LLM observability skill for Claude Code and Codex.",
      "keywords": ["langfuse", "observability", "traces", "llm", "prompts", "datasets", "debugging", "telemetry", "mcp"]
    },
    {
      "name": "sabx",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/sabx.git"
      },
      "description": "SABnzbd CLI - control downloads, queues, RSS feeds, speed limits, and automation via terminal. Full API parity with SABnzbd REST API.",
      "keywords": ["sabnzbd", "usenet", "downloads", "nzb", "queue", "automation", "cli", "rss"]
    },
    {
      "name": "bkt",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/bitbucket-cli.git"
      },
      "description": "Bitbucket CLI - manage repositories, pull requests, branches, issues, webhooks, and pipelines for both Data Center and Cloud. Mirrors gh ergonomics with JSON/YAML output.",
      "keywords": ["bitbucket", "git", "pr", "pull-request", "ci", "pipeline", "devops", "cli", "datacenter", "cloud"]
    },
    {
      "name": "jk",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/jenkins-cli.git"
      },
      "description": "Jenkins CLI - GitHub CLI-style interface for Jenkins controllers. Manage jobs, pipelines, runs, logs, artifacts, credentials, nodes, queues, and plugins.",
      "keywords": ["jenkins", "ci", "cd", "pipeline", "builds", "devops", "cli", "automation"]
    },
    {
      "name": "yoetz",
      "source": {
        "source": "url",
        "url": "https://github.com/avivsinai/yoetz.git"
      },
      "description": "LLM council, bundler, and multimodal gateway - multi-model consensus, code review, image/video generation, and browser fallback for coding agents.",
      "keywords": ["llm", "council", "multimodal", "code-review", "bundler", "ai", "cli", "agents"]
    }
  ]
}
