      "version": "1.0.0",
      "author": {
        "name": "Tiago Bastos da Silva",
        "email": "code@bastos.dev"
      },
      "license": "MIT",
      "tags": ["ruby", "metaprogramming", "dsl", "stdlib", "gems", "performance", "patterns", "rake", "bundler"],
