{
  "name": "apex",
  "version": "0.5.8",
  "description": "Pattern discovery, task tracking, and continuous learning for AI coding assistants. Provides 13 MCP tools for intelligent memory, context-aware pattern lookup, trust-scored recommendations, and comprehensive pattern browsing.",
  "author": {
    "name": "Ben",
    "url": "https://github.com/benredmond"
  },
  "homepage": "https://github.com/benredmond/apex",
  "repository": "https://github.com/benredmond/apex",
  "license": "MIT",
  "keywords": [
    "ai",
    "patterns",
    "mcp",
    "intelligence",
    "learning",
    "workflow",
    "automation"
  ],
  "mcpServers": {
    "apex": {
      "command": "npx",
      "args": [
        "@benredmond/apex",
        "mcp",
        "serve"
      ],
      "env": {}
    }
  }
}
