{
  "name": "cc-plugins",
  "owner": {
    "name": "Ryo Higasigawa"
  },
  "plugins": [
    {
      "name": "claude-code-spec",
      "source": "./plugins/claude-code-spec",
      "description": "Claude Code CLI の仕様と使い方を完璧に理解するための知識プラグイン"
    },
    {
      "name": "codex-cli-spec",
      "source": "./plugins/codex-cli-spec",
      "description": "OpenAI Codex CLI の仕様と使い方を完璧に理解するための知識プラグイン"
    },
    {
      "name": "gemini-cli-spec",
      "source": "./plugins/gemini-cli-spec",
      "description": "Gemini CLI の仕様と使い方を完璧に理解するための知識プラグイン"
    },
    {
      "name": "git-actions",
      "source": "./plugins/git-actions",
      "description": "Git commit and push workflow management for Claude Code"
    },
    {
      "name": "plugin-generator",
      "source": "./plugins/plugin-generator",
      "description": "Claude Code プラグインのスキャフォールディングとバリデーション"
    },
    {
      "name": "web-search-gemini",
      "source": "./plugins/web-search-gemini",
      "description": "Gemini CLI を活用した Web 検索プラグイン。技術調査、汎用リサーチ、最新情報取得に対応。"
    },
    {
      "name": "plugin-updater",
      "source": "./plugins/plugin-updater",
      "description": "マーケットプレイスとインストール済みプラグインを一括更新"
    },
    {
      "name": "cursor-cli-spec",
      "source": "./plugins/cursor-cli-spec",
      "description": "Cursor IDE および cursor-agent CLI の仕様と使い方を完璧に理解するための知識プラグイン"
    },
    {
      "name": "wrangler-cli-spec",
      "source": "./plugins/wrangler-cli-spec",
      "description": "Cloudflare Wrangler CLI の仕様と使い方に関する知識を提供。Durable Objects, Queues, Hyperdrive, Service Bindings, カスタムビルド設定 (Webpack/esbuild) などについて回答。"
    },
    {
      "name": "cf-terraforming-spec",
      "source": "./plugins/cf-terraforming-spec",
      "description": "Cloudflare cf-terraforming CLI の仕様と使い方に関する知識を提供。既存の Cloudflare リソースを Terraform にインポートする方法、HCL 設定の生成、インポートブロックの使用などについて回答。"
    },
    {
      "name": "cloudflare-knowledge",
      "source": "./plugins/cloudflare-knowledge",
      "description": "Cloudflare のサービス、Wrangler CLI、Workers/Pages 開発、Terraform 管理、セキュリティ機能に関する包括的な知識プラグイン"
    },
    {
      "name": "memory-optimizer",
      "source": "./plugins/memory-optimizer",
      "description": "Claude Code のメモリ管理（CLAUDE.md、rules、imports）を最適化するための知識とワークフローを提供するプラグイン"
    },
    {
      "name": "agent-browser-spec",
      "source": "./plugins/agent-browser-spec",
      "description": "agent-browser CLI の仕様と使い方に関する知識を提供。ヘッドレスブラウザ自動化、スナップショット、セレクター、セッション管理、ネットワーク制御について回答。"
    },
    {
      "name": "nano-banana-image",
      "source": "./plugins/nano-banana-image",
      "description": "Gemini Nano Banana Pro (gemini-3-pro-image-preview) を活用した画像生成プラグイン。テキストから画像生成、解像度・アスペクト比指定、参照画像を使った編集に対応。"
    },
    {
      "name": "web-search-codex",
      "source": "./plugins/web-search-codex",
      "description": "Codex CLI 内で Gemini CLI を活用した Web 検索を実行。--full-auto モードで自動検索対応。"
    },
    {
      "name": "web-search-unified",
      "source": "./plugins/web-search-unified",
      "description": "Gemini、Codex、WebSearch を並列実行し結果を統合する高精度 Web 検索"
    },
    {
      "name": "gogcli-spec",
      "source": "./plugins/gogcli-spec",
      "description": "Google Suite CLI (gogcli/gog) の仕様と使い方を完璧に理解するための知識プラグイン。Gmail、Calendar、Drive、Contacts、Tasks、Sheets、Docs、Chat、Classroom 等の操作について回答。"
    }
  ]
}
