{
  "name": "codemate",
  "owner": {
    "name": "CodeMate"
  },
  "metadata": {
    "description": "CodeMate plugin marketplace for Git and PR workflows",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "git",
      "source": "./plugins/git",
      "description": "Git workflow management tools for staging, committing, and pushing changes",
      "version": "1.0.0",
      "category": "git",
      "keywords": ["git", "commit", "push", "workflow"]
    },
    {
      "name": "pr",
      "source": "./plugins/pr",
      "description": "GitHub Pull Request workflow management with skills for creating, updating, and managing PRs",
      "version": "1.0.0",
      "category": "github",
      "keywords": ["github", "pull-request", "pr", "review"]
    },
    {
      "name": "dev",
      "source": "./plugins/dev",
      "description": "Development utilities including environment variable key management",
      "version": "1.0.0",
      "category": "development",
      "keywords": ["dev", "environment", "variables", "utilities"]
    },
    {
      "name": "issue",
      "source": "./plugins/issue",
      "description": "GitHub Issue workflow management with skills for reading, refining, triaging, and classifying issues",
      "version": "1.0.0",
      "category": "github",
      "keywords": ["github", "issue", "labels", "triage", "workflow"]
    },
    {
      "name": "session",
      "source": "./plugins/session",
      "description": "Session lifecycle hooks for tracking activity and sending Slack notifications on code changes",
      "version": "1.0.0",
      "category": "codemate",
      "keywords": ["session", "hooks", "slack", "notifications", "lifecycle"]
    },
    {
      "name": "pm",
      "source": "./plugins/pm",
      "description": "Spec-driven project management with skills for creating specs, planning, decomposing tasks, and syncing to GitHub Issues",
      "version": "1.0.0",
      "category": "project-management",
      "keywords": ["project-management", "spec", "planning", "issues", "workflow"]
    }
  ]
}
