files..claude-plugin/plugin.json:
{
  "name": "claude-journal",
  "description": "Lightweight journal/memory system with zero ML dependencies. SQLite-based with natural language time queries, auto-capture, and smart context recovery.",
  "version": "0.1.0",
  "author": {
    "name": "Chris Bryant"
  },
  "homepage": "https://github.com/chrismbryant/claude-journal-mcp",
  "repository": "https://github.com/chrismbryant/claude-journal-mcp",
  "keywords": [
    "journal",
    "memory",
    "sqlite",
    "mcp",
    "lightweight",
    "no-ml",
    "context",
    "auto-capture"
  ],
  "license": "MIT",
  "mcpServers": {
    "journal": {
      "command": "python",
      "args": ["-m", "claude_journal.server"]
    }
  }
}
