{
  "name": "playtest",
  "version": "3.0.0",
  "description": "AI-driven game playtesting framework with TypeScript engine orchestration and parallel player agents",
  "author": {
    "name": "Christopher de Beer"
  },
  "repository": "https://github.com/christopherdebeer/playtest",
  "license": "MIT",
  "keywords": ["game", "playtesting", "multi-agent", "claude", "testing", "simulation"],
  "hooks": {
    "SessionStart": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event SessionStart"
          }
        ]
      }
    ],
    "UserPromptSubmit": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event UserPromptSubmit"
          }
        ]
      }
    ],
    "PreToolUse": [
      {
        "matcher": "Task",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PreToolUse --matcher Task"
          }
        ]
      },
      {
        "matcher": "*",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PreToolUse --matcher all"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "*",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PostToolUse --matcher all"
          }
        ]
      }
    ],
    "SubagentStart": [
      {
        "matcher": "player",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook --name start --agent player"
          }
        ]
      },
      {
        "matcher": "gamemaster",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook --name start --agent gamemaster"
          }
        ]
      },
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event SubagentStart"
          }
        ]
      }
    ],
    "SubagentStop": [
      {
        "matcher": "player",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook --name stop --agent player"
          }
        ]
      },
      {
        "matcher": "gamemaster",
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook --name stop --agent gamemaster"
          }
        ]
      },
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event SubagentStop"
          }
        ]
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event Stop"
          }
        ]
      }
    ],
    "PreCompact": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PreCompact"
          }
        ]
      }
    ],
    "SessionEnd": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event SessionEnd"
          }
        ]
      }
    ],
    "Notification": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event Notification"
          }
        ]
      }
    ],
    "PermissionRequest": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PermissionRequest"
          }
        ]
      }
    ],
    "PostToolUseFailure": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "./playtest hook-event --event PostToolUseFailure"
          }
        ]
      }
    ]
  }
}
