{
  "name": "opinionated-basic-cc-setup",
  "owner": {
    "name": "YeonGyu-Kim",
    "email": "code.yeon.gyu@gmail.com"
  },
  "metadata": {
    "description": "Production-tested Claude Code plugin with intelligent context injection, language-specific guides, and project knowledge management",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "opinionated-basic-cc-setup",
      "source": "./",
      "description": "Opinionated basic Claude Code setup with intelligent context injection, language-specific guides, project knowledge, and encoding validation",
      "version": "1.0.0",
      "author": {
        "name": "YeonGyu-Kim",
        "email": "code.yeon.gyu@gmail.com"
      },
      "homepage": "https://github.com/code-yeongyu/opinionated-basic-cc-setup",
      "repository": "https://github.com/code-yeongyu/opinionated-basic-cc-setup",
      "license": "MIT",
      "keywords": [
        "enhanced-basic",
        "context-injection",
        "language-guides",
        "project-knowledge",
        "hooks",
        "validation",
        "python",
        "svelte",
        "typescript",
        "static-analysis"
      ],
      "category": "productivity",
      "hooks": {
        "PostToolUse": [
          {
            "matcher": "Read|Write|Edit|MultiEdit",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/inject_language_guide.py"
              }
            ]
          },
          {
            "matcher": "Read|Write|Edit|MultiEdit|NotebookEdit",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/inject_knowledge.py"
              }
            ]
          },
          {
            "matcher": "Write|Edit|MultiEdit|NotebookEdit",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/check_corrupted_encoding.py"
              }
            ]
          },
          {
            "matcher": "Write|Edit|MultiEdit",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/static_check.py"
              }
            ]
          },
          {
            "matcher": "Bash",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/suggest_terminalcp_for_bash.py"
              }
            ]
          },
          {
            "matcher": "mcp__terminalcp__terminalcp",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/post-tool-use/terminalcp_list_on_start.py"
              }
            ]
          }
        ],
        "Stop": [
          {
            "matcher": ".*",
            "hooks": [
              {
                "type": "command",
                "command": "${CLAUDE_PLUGIN_ROOT}/hooks/stop/check_todos_completed.py"
              }
            ]
          }
        ]
      }
    }
  ]
}

