{
  "name": "claude-session-summary",
  "version": "1.0.1",
  "description": "Generates running session summaries on stop, viewable via tmux popup",
  "hooks": {
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/scripts/session-summary.zsh",
            "timeout": 5
          }
        ]
      }
    ]
  }
}
