{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "devnogari-claude-plugins",
  "version": "2.11.3",
  "description": "Development workflow plugins for Claude Code: Slack notifications, PR review automation, Feature workflow, Code patterns enforcement, and Best practices - all load directly into session",
  "owner": {
    "name": "DevNogari",
    "email": "re2rar@gmail.com"
  },
  "plugins": [
    {
      "name": "slack-notify",
      "description": "Real-time Slack notifications for task completions with repository context and git changes tracking",
      "source": "./plugins/slack-notify",
      "version": "1.2.7",
      "author": {
        "name": "DevNogari",
        "email": "re2rar@gmail.com"
      },
      "keywords": ["slack", "notifications", "automation"]
    },
    {
      "name": "pr-review-automation",
      "description": "Automated PR review feedback processing with Gemini bot integration, priority filtering, and Ralph-loop autonomous processing",
      "source": "./plugins/pr-review-automation",
      "version": "1.2.0",
      "author": {
        "name": "DevNogari",
        "email": "re2rar@gmail.com"
      },
      "keywords": ["pr-review", "gemini", "automation", "ralph-loop"]
    },
    {
      "name": "feature-workflow",
      "description": "TDD-based feature workflow (7 phases) with 4 core principles: Subagent, LSP, TDD, Parallel. Auto-activates on feature implementation",
      "source": "./plugins/feature-workflow",
      "version": "1.9.4",
      "author": {
        "name": "DevNogari",
        "email": "re2rar@gmail.com"
      },
      "keywords": ["feature-workflow", "tdd", "git-worktree", "lsp", "ralph-loop", "feature-dev", "phase-enforcement", "react", "go", "flutter"]
    },
    {
      "name": "code-patterns",
      "description": "Enforces consistent code patterns: LSP-first exploration, subagent delegation, and mandatory code review",
      "source": "./plugins/code-patterns",
      "version": "2.0.7",
      "author": {
        "name": "DevNogari",
        "email": "re2rar@gmail.com"
      },
      "keywords": ["code-patterns", "lsp", "subagent", "code-review", "best-practices"]
    },
    {
      "name": "best-practices",
      "description": "Comprehensive best practices (40+ rules per framework). Auto-activates based on project type: React/Vite, Next.js, Go/Gin, Flutter, FastAPI, Kotlin Multiplatform, Kotlin Spring (Coroutines + WebFlux)",
      "source": "./plugins/best-practices",
      "version": "2.8.2",
      "author": {
        "name": "DevNogari",
        "email": "re2rar@gmail.com"
      },
      "keywords": ["best-practices", "react", "nextjs", "go", "uber-fx", "flutter", "python", "fastapi", "kotlin-multiplatform", "kotlin-spring", "coroutines", "webflux", "r2dbc", "compose", "koin", "ktor", "performance", "patterns"]
    }
  ]
}
