{
  "name": "my-claude-plugins",
  "owner": {
    "name": "Christian Pedersen",
    "email": "christian@dkmaker.xyz"
  },
  "metadata": {
    "description": "Various life improvement plugins for Claude Code",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "claude-expert",
      "source": "./claude-expert",
      "description": "Expert knowledge system for Claude Code with complete documentation database, intelligent search, and automatic CLI management. Provides authoritative answers to all Claude Code questions using official documentation.",
      "keywords": ["documentation", "expert", "knowledge", "claude-code", "help", "search", "nodejs"],
      "category": "knowledge"
    },
    {
      "name": "perplexity",
      "source": "./perplexity",
      "description": "Real-time web search, reasoning, and research through Perplexity's API via MCP server",
      "keywords": ["mcp", "search", "web-search", "perplexity", "research", "reasoning", "ai"],
      "category": "productivity"
    },
    {
      "name": "playwright",
      "source": "./playwright",
      "description": "Browser automation and testing with Playwright via MCP server",
      "keywords": ["mcp", "playwright", "browser", "automation", "testing", "e2e", "web"],
      "category": "testing"
    },
    {
      "name": "baseline",
      "source": "./baseline",
      "description": "Ensures optimal Claude Code defaults and validates required tools. Includes custom statusline and recommended settings.",
      "keywords": ["configuration", "defaults", "tools", "validation", "statusline", "settings"],
      "category": "productivity"
    },
    {
      "name": "transcript",
      "source": "./transcript",
      "description": "Parse and analyze Claude Code session transcripts with beautiful HTML output. List transcripts, generate reports, and explore conversation history.",
      "keywords": ["transcript", "session", "debug", "history", "analysis", "html", "report"],
      "category": "productivity"
    },
    {
      "name": "wsl-ssh-agent",
      "source": "./wsl-ssh-agent",
      "description": "Bridges Windows SSH agent to WSL for SSH/SCP/Git operations. Works with 1Password, native Windows OpenSSH, and other SSH agent implementations.",
      "keywords": ["wsl", "ssh", "ssh-agent", "1password", "git", "windows"],
      "category": "productivity"
    },
    {
      "name": "mcp-rest-api",
      "source": {
        "source": "github",
        "repo": "dkmaker/mcp-rest-api"
      },
      "description": "REST API tester MCP server - test HTTP endpoints directly from Claude Code",
      "version": "0.4.0",
      "keywords": ["mcp", "rest", "api", "http", "testing", "development"],
      "category": "productivity"
    },
    {
      "name": "image-tools",
      "source": "./image-tools",
      "description": "Complete image toolkit: AI generation with Gemini and manipulation with Pillow (resize, crop, convert, alpha, transform, analyze).",
      "keywords": ["image", "generation", "ai", "gemini", "resize", "crop", "convert", "alpha", "pillow", "manipulation"],
      "category": "creative"
    },
    {
      "name": "my-plugin-dev",
      "source": "./my-plugin-dev",
      "description": "Development toolkit for maintaining and developing the my-claude-plugins marketplace",
      "keywords": ["development", "plugin-dev", "toolkit", "maintenance"],
      "category": "development"
    },
    {
      "name": "superpowers",
      "source": "./superpowers",
      "description": "Core skills library: TDD, debugging, collaboration patterns, and proven techniques (forked from obra/superpowers for local customization)",
      "keywords": ["skills", "tdd", "debugging", "collaboration", "best-practices", "workflows"],
      "category": "productivity"
    },
    {
      "name": "playwright-cli",
      "source": "./playwright-cli",
      "description": "Browser automation using playwright-cli for web testing, form filling, screenshots, and data extraction",
      "keywords": ["playwright", "browser", "automation", "testing", "web", "screenshot"],
      "category": "testing"
    },
    {
      "name": "web-search",
      "source": "./web-search",
      "description": "Web search using Perplexity AI for search, research, reasoning, and question answering with result archiving",
      "keywords": ["search", "perplexity", "research", "web", "ai"],
      "category": "productivity"
    }
  ]
}
