files..claude-plugin/plugin.json:
{
  "name": "mags",
  "version": "0.4.1",
  "description": "Memory And Guidance System — Project documentation memory, session persistence, and development guidance for Claude Code",
  "author": {
    "name": "Dogancan",
    "url": "https://github.com/doancan"
  },
  "repository": "https://github.com/doancan/mags",
  "homepage": "https://github.com/doancan/mags",
  "license": "MIT",
  "keywords": ["documentation", "memory", "project-management", "guidance", "session-persistence"],
  "mcpServers": {
    "mags": {
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/server/start.js"],
      "env": {
        "MAGS_EMBEDDING_PROVIDER": "local"
      }
    }
  }
}
