{
  "name": "doarakko-config",
  "description": "Productivity commands for branch management, commit splitting, PR workflows, code review, CI/CD automation, and test creation",
  "version": "1.0.0",
  "author": {
    "name": "Doarakko",
    "email": "2wua4nlyi@gmail.com"
  },
  "hooks": {
    "PostToolUse": [
      {
        "matcher": "Edit|Write",
        "hooks": [
          {
            "type": "command",
            "command": "if [ -f Makefile ] && grep -q '^lint:' Makefile; then make lint 2>&1 | tail -20; fi"
          }
        ],
        "description": "Run make lint after file edits"
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "bash ${CLAUDE_PLUGIN_ROOT}/hooks/task-completion-notify.sh"
          }
        ]
      }
    ],
    "Notification": [
      {
        "matcher": "idle_prompt",
        "hooks": [
          {
            "type": "command",
            "command": "bash ${CLAUDE_PLUGIN_ROOT}/hooks/idle-notify.sh"
          }
        ]
      }
    ]
  }
}
