{
  "name": "claude-reliability",
  "description": "Reliability hooks for Claude Code - prevents common mistakes and enforces quality standards",
  "version": "26.217.1",
  "author": {
    "name": "David R. MacIver",
    "email": "david@drmaciver.com"
  },
  "repository": "https://github.com/DRMacIver/claude-reliability",
  "license": "MIT",
  "keywords": ["reliability", "hooks", "quality", "code-review"],
  "commands": "./commands/",
  "skills": "./skills/",
  "hooks": {
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/scripts/hooks/hook.sh stop"
          }
        ]
      }
    ],
    "UserPromptSubmit": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/scripts/hooks/hook.sh user-prompt-submit"
          }
        ]
      }
    ],
    "PreToolUse": [
      {
        "matcher": ".*",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/scripts/hooks/hook.sh pre-tool-use"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": ".*",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/scripts/hooks/hook.sh post-tool-use"
          }
        ]
      }
    ]
  }
}
