{
  "name": "virtuoso-marketplace",
  "owner": {
    "name": "Virtuoso Team"
  },
  "plugins": [
    {
      "name": "virtuoso",
      "description": "Transform Claude CLI into an autonomous software house using a swarm of specialized agents, strict memory protocols, and automated hooks.",
      "source": "./"
    }
  ]
}

files.hooks/hooks.json:
{
  "SessionStart": [
    {
      "matcher": ".*",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/session_init.py",
          "timeout": 15
        }
      ]
    }
  ],
  "PreToolUse": [
    {
      "matcher": "Bash",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/validate_stack.py",
          "timeout": 30
        }
      ]
    },
    {
      "matcher": "Write|Edit|StrReplace",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/secret_scanner.py",
          "timeout": 10
        }
      ]
    }
  ],
  "PostToolUse": [
    {
      "matcher": "Write|Edit|StrReplace",
      "hooks": [
        {
          "type": "command",
          "command": "bash -c 'echo \"[$(date +%Y-%m-%dT%H:%M:%S)] Modified: ${TOOL_FILE_PATH}\" >> \"${CLAUDE_PROJECT_DIR}/.claude/audit.log\" 2>/dev/null || true'",
          "timeout": 10
        }
      ]
    }
  ],
  "SubagentStart": [
    {
      "matcher": ".*",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/context_injector.py",
          "timeout": 10
        }
      ]
    }
  ],
  "SubagentStop": [
    {
      "matcher": ".*",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/autonomy_loop.py",
          "timeout": 15
        }
      ]
    }
  ],
  "PreLaunch": [
    {
      "matcher": ".*",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/validate_launch_readiness.py",
          "timeout": 30,
          "description": "Validates pre-launch requirements (compliance, marketing, release plan)"
        }
      ]
    }
  ],
  "PostLaunch": [
    {
      "matcher": ".*",
      "hooks": [
        {
          "type": "command",
          "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/setup_analytics.py",
          "timeout": 20,
          "description": "Sets up analytics tracking after launch"
        }
      ]
    }
  ]
}
