{
  "name": "epochtime-ai-plugins",
  "description": "Official Claude Code plugin marketplace by Epochtime AI - A curated collection of plugins to enhance your AI-powered development workflow",
  "metadata": {
    "description": "Epochtime AI's official marketplace providing comprehensive plugins for documentation crawling, database schema management with Atlas, and other development tools"
  },
  "version": "1.0.0",
  "owner": {
    "name": "Epochtime AI",
    "email": "contact@epochtime.ai"
  },
  "repository": "https://github.com/EpochTime-AI/cc-plugins",
  "plugins": [
    {
      "name": "doc-crawler-plugin",
      "source": "./doc-crawler-plugin",
      "description": "Systematic documentation crawler that converts online documentation into well-structured Claude Code skills",
      "version": "1.0.0",
      "category": "documentation",
      "keywords": ["documentation", "crawler", "web-scraping", "inform", "skills", "markdown"],
      "author": {
        "name": "c_w_xiaohei",
        "email": "contact@epochtime.ai"
      }
    },
    {
      "name": "atlas-db-plugin",
      "source": "./atlas-db-plugin",
      "description": "Comprehensive Atlas database schema migration tool integration with LSP support. Learn declarative and versioned workflows, schema definition, CI/CD integration, and ORM support",
      "version": "1.1.0",
      "category": "database",
      "keywords": ["atlas", "database", "migration", "schema", "declarative", "versioned", "cicd", "orm", "lsp", "hcl"],
      "author": {
        "name": "c_w_xiaohei",
        "email": "contact@epochtime.ai"
      },
      "lspServers": {
        "atlas": {
          "command": "atlas-ls",
          "args": ["--stdio"],
          "extensionToLanguage": {
            ".hcl": "hcl",
            ".atlas.hcl": "hcl"
          },
          "transport": "stdio",
          "workspaceFolder": "${CLAUDE_PROJECT_DIR}",
          "startupTimeout": 10000,
          "restartOnCrash": true,
          "maxRestarts": 3
        }
      }
    },
    {
      "name": "claude-code-doc-plugin",
      "source": "./claude-code-doc-plugin",
      "description": "Complete Claude Code official documentation reference with 48 docs covering setup, plugins, MCP, enterprise deployment, and troubleshooting",
      "version": "1.0.0",
      "category": "documentation",
      "keywords": ["claude-code", "documentation", "reference", "plugins", "mcp", "setup", "enterprise", "troubleshooting"],
      "author": {
        "name": "Epochtime AI",
        "email": "contact@epochtime.ai"
      }
    }
  ]
}
