{
  "name": "feedmob-agent-skills",
  "version": "1.0.0",
  "description": "A collection of skills for agents",
  "author": "FeedMob",
  "type": "skill-collection",
  "skills": [
    {
      "name": "feedmob-brand-guidelines",
      "description": "Generate FeedMob-branded content including reports, presentations, charts, and artifacts following official brand guidelines. Use when creating any FeedMob-branded materials.",
      "path": "skills/feedmob-brand-guidelines"
    },
    {
      "name": "feedmob-presentations",
      "description": "Create, edit, and analyze PowerPoint presentations with professional styling, themes, and layouts. Supports python-pptx for creation and OOXML manipulation for advanced editing.",
      "path": "skills/feedmob-presentations"
    },
    {
      "name": "url-parameter-parser",
      "description": "Parse URLs in CSV files and extract query parameters as new columns. Use when working with CSV files containing URLs that need parameter extraction and analysis.",
      "path": "skills/url-parameter-parser"
    },
    {
      "name": "ai-news-daily",
      "description": "Automatically fetch and summarize the latest AI news, research, and industry developments. Use when users request: (1) Daily AI news updates, (2) Latest AI technology developments, (3) Recent AI research papers or breakthroughs, (4) AI industry trends and market news, (5) Specific AI company announcements, or (6) Automated daily AI briefings. Supports customizable search queries, multi-source aggregation, and formatted output in various styles.",
      "path": "skills/ai-news-daily"
    },
    {
      "name": "gemini-image-generator",
      "description": "Generate, edit, or transform images with Gemini using bundled Python scripts (Flash or Pro) including aspect ratio, resolution, image-to-image edits, logo overlays, and reference images. Use when users request image generation, image edits, image-to-image transformations, logo placement, or specific aspect ratios or resolutions.",
      "path": "skills/gemini-image-generator"
    },
    {
      "name": "civitai-analyst",
      "description": "Generate and execute SQL queries against the civitai_records PostgreSQL database to analyze video performance on Civitai. Use when users ask about video engagement metrics, content performance analysis, tag/theme analysis, quality scores, weekly reports, comparing videos, or content recommendations.",
      "path": "skills/civitai-analyst"
    }
  ]
}

