{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "fprochazka-claude-code-plugins",
  "description": "Collection of Claude Code plugins by Filip Procházka",
  "owner": {
    "name": "Filip Procházka",
    "url": "https://github.com/fprochazka"
  },
  "plugins": [
    {
      "name": "skill-keyword-reminder",
      "description": "Automatically reminds Claude to load relevant skills when keyword triggers appear in user prompts",
      "version": "1.1.0",
      "author": {
        "name": "Filip Procházka",
        "url": "https://github.com/fprochazka"
      },
      "source": "./plugins/skill-keyword-reminder",
      "category": "productivity",
      "homepage": "https://github.com/fprochazka/claude-code-plugins/tree/main/plugins/skill-keyword-reminder"
    },
    {
      "name": "gemini-cli",
      "description": "Skill for using Gemini CLI with massive context windows (1M tokens) for codebase analysis and second opinions",
      "version": "1.0.0",
      "author": {
        "name": "Filip Procházka",
        "url": "https://github.com/fprochazka"
      },
      "source": "./plugins/gemini-cli",
      "category": "productivity",
      "homepage": "https://github.com/fprochazka/claude-code-plugins/tree/main/plugins/gemini-cli"
    },
    {
      "name": "no-background-tasks",
      "description": "Enforces serial execution of Bash and Task tools within a session",
      "version": "1.0.0",
      "author": {
        "name": "Filip Procházka",
        "url": "https://github.com/fprochazka"
      },
      "source": "./plugins/no-background-tasks",
      "category": "productivity",
      "homepage": "https://github.com/fprochazka/claude-code-plugins/tree/main/plugins/no-background-tasks"
    },
    {
      "name": "glab-mr",
      "description": "GitLab MR tools - fix failed CI, resolve comments, and more",
      "version": "1.0.0",
      "author": {
        "name": "Filip Procházka",
        "url": "https://github.com/fprochazka"
      },
      "source": "./plugins/glab-mr",
      "category": "productivity",
      "homepage": "https://github.com/fprochazka/claude-code-plugins/tree/main/plugins/glab-mr"
    }
  ]
}

