{
  "name": "feelsclaudeman",
  "version": "1.0.0",
  "description": "Turn Claude's inner monologue into a meme-worthy reality show",
  "hooks": "./hooks/hooks.json",
  "commands": "./commands",
  "mcpServers": {
    "feelsclaudeman": {
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/mcp-server/build/index.js"],
      "env": {
        "FEELS_PORT": "38470",
        "FEELS_UI_PORT": "38480",
        "DB_PATH": "${CLAUDE_PLUGIN_ROOT}/data/feelsclaudeman.db"
      }
    }
  }
}
