{
  "name": "hidetsugu-miya",
  "owner": {
    "name": "miya"
  },
  "metadata": {
    "description": "Custom Claude Code plugins by miya",
    "homepage": "https://github.com/hidetsugu-miya/claude-plugins"
  },
  "plugins": [
    {
      "name": "cocoindex",
      "version": "0.7.1",
      "source": "./cocoindex",
      "description": "コードベースのベクトルインデックス構築・検索プラグイン。CocoIndexを使って自然言語クエリで関連コードのエントリーポイントを発見する。"
    },
    {
      "name": "context7",
      "version": "0.2.0",
      "source": "./context7",
      "description": "ライブラリの最新ドキュメントを取得するプラグイン。Context7 MCPサーバーを使ってパッケージ名からIDを解決し、バージョン固有のドキュメントを参照する。"
    },
    {
      "name": "rollbar",
      "version": "0.3.0",
      "source": "./rollbar",
      "description": "Rollbarのエラートラッキングデータを取得・管理するプラグイン。@rollbar/mcp-serverを使ってアイテム詳細、デプロイ情報、トップエラーの確認・更新を行う。"
    },
    {
      "name": "sentry",
      "version": "0.4.0",
      "source": "./sentry",
      "description": "Sentryのエラートラッキングデータを取得・管理するプラグイン。@sentry/mcp-serverを使ってイシュー詳細、プロジェクト情報、エラー分析を行う。"
    },
    {
      "name": "figma",
      "version": "0.3.0",
      "source": "./figma",
      "description": "Figmaデザインファイルの取得・コード生成プラグイン。Figma Dev Mode MCPサーバーと連携してデザインからReact+Tailwindコードを自動生成する。"
    },
    {
      "name": "playwright",
      "version": "0.3.0",
      "source": "./playwright",
      "description": "Playwright MCPを使ったブラウザ自動化プラグイン。Webページのナビゲート、スナップショット取得、要素クリックなどのブラウザ操作をHTTPサーバーモードで実行する。"
    },
    {
      "name": "claude-mem",
      "version": "1.1.1",
      "source": "./claude-mem",
      "description": "claude-mem永続メモリの検索・取得プラグイン。Worker HTTP API経由で過去のセッション情報、観察、タイムラインを参照する。"
    },
    {
      "name": "devin",
      "version": "0.1.0",
      "source": "./devin",
      "description": "Devin MCP/DeepWiki経由でGitHubリポジトリ（プライベート含む）のドキュメント構造取得・内容取得・質問応答を行うプラグイン。"
    },
    {
      "name": "chrome-devtools",
      "version": "0.1.1",
      "source": "./chrome-devtools",
      "description": "Chrome DevTools MCPを使ったブラウザ自動化・デバッグプラグイン。DOMスナップショット、スクリーンショット、コンソールログ、ネットワーク監視、パフォーマンス分析などをHTTPサーバーモードで実行する。"
    }
  ]
}
