{
  "name": "claude-ruby-marketplace",
  "metadata": {
    "description": "Ruby and Rails plugins: LSP integration, RSpec testing patterns, ActiveRecord queries, Draper decorators, MCP server development, DragonRuby game development, and RatatuiRuby TUI development"
  },
  "owner": {
    "name": "hoblin"
  },
  "plugins": [
    {
      "name": "ruby-lsp",
      "description": "Ruby language server (Shopify's ruby-lsp) for code intelligence",
      "version": "1.0.0",
      "source": "./plugins/ruby-lsp",
      "category": "development"
    },
    {
      "name": "rspec",
      "description": "RSpec and FactoryBot testing patterns for Rails",
      "version": "1.1.1",
      "source": "./plugins/rspec",
      "category": "testing"
    },
    {
      "name": "draper",
      "description": "Draper decorator patterns for Rails view logic",
      "version": "1.1.1",
      "source": "./plugins/draper",
      "category": "patterns"
    },
    {
      "name": "activerecord",
      "description": "ActiveRecord patterns for Rails models and queries",
      "version": "1.0.1",
      "source": "./plugins/activerecord",
      "category": "database"
    },
    {
      "name": "mcp",
      "description": "MCP server development with Ruby SDK",
      "version": "1.0.1",
      "source": "./plugins/mcp",
      "category": "integrations"
    },
    {
      "name": "dragonruby",
      "description": "DragonRuby Game Toolkit patterns for 2D game development",
      "version": "1.0.4",
      "source": "./plugins/dragonruby",
      "category": "game-development"
    },
    {
      "name": "ratatui-ruby",
      "description": "RatatuiRuby TUI development for terminal user interfaces",
      "version": "1.2.1",
      "source": "./plugins/ratatui-ruby",
      "category": "tui-development"
    },
    {
      "name": "rpi",
      "description": "RPI workflow: Research, Planning, Implementation with context engineering",
      "version": "1.4.0",
      "source": "./plugins/rpi",
      "category": "workflow"
    }
  ]
}
