{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "ekstend",
  "version": "1.11.0",
  "description": "A collection of plugins for Claude Code including sequential thinking tools.",
  "owner": {
    "name": "Husni Adil Makmur",
    "email": "husni.adil@gmail.com"
  },
  "plugins": [
    {
      "name": "ultrathink",
      "description": "Sequential thinking CLI for multi-step problem solving with confidence tracking and assumption management.",
      "version": "2.0.2",
      "author": {
        "name": "Husni Adil Makmur",
        "email": "husni.adil@gmail.com"
      },
      "license": "MIT",
      "source": "./plugins/ultrathink",
      "category": "development"
    },
    {
      "name": "skill-creator",
      "description": "Guide for creating effective skills that extend Claude's capabilities with specialized knowledge, workflows, or tool integrations.",
      "version": "1.0.1",
      "author": {
        "name": "Anthropic",
        "url": "https://github.com/anthropics/skills"
      },
      "license": "Apache-2.0",
      "source": "./plugins/skill-creator",
      "category": "development"
    },
    {
      "name": "database",
      "description": "Access MySQL, PostgreSQL, SQLite databases, and Redis key-value stores via CLI for querying, schema/key exploration, and data management.",
      "version": "1.2.2",
      "author": {
        "name": "Husni Adil Makmur",
        "email": "husni.adil@gmail.com"
      },
      "license": "MIT",
      "source": "./plugins/database",
      "category": "database"
    },
    {
      "name": "ideate",
      "description": "Facilitation-first brainstorming skill that helps users unlock their own ideas through structured questioning and expansion techniques.",
      "version": "1.0.0",
      "author": {
        "name": "Husni Adil Makmur",
        "email": "husni.adil@gmail.com"
      },
      "license": "MIT",
      "source": "./plugins/ideate",
      "category": "productivity"
    },
    {
      "name": "code-review",
      "description": "Iterative code review with gap detection and user-controlled fixes. Supports PR review (diff-based) and holistic codebase review. Reviews code, identifies issues, and fixes them in cycles until clean.",
      "version": "1.0.1",
      "author": {
        "name": "Husni Adil Makmur",
        "email": "husni.adil@gmail.com"
      },
      "license": "MIT",
      "source": "./plugins/code-review",
      "category": "development"
    },
    {
      "name": "interview",
      "description": "Deep requirements gathering skill that interviews users through thoughtful, in-depth questions before implementation. Uncovers hidden requirements, edge cases, and trade-offs.",
      "version": "1.0.0",
      "author": {
        "name": "Husni Adil Makmur",
        "email": "husni.adil@gmail.com"
      },
      "license": "MIT",
      "source": "./plugins/interview",
      "category": "productivity"
    }
  ]
}
