{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "claude-toolbox",
  "metadata": {
    "description": "Claude Code plugins for development workflows: Makefile generation, CI workflow generation, pre-commit linting, Figma design application, semgrep security review, Go development, and gabyx/Githooks setup"
  },
  "owner": {
    "name": "Jaehyun Yeom"
  },
  "plugins": [
    {
      "name": "makefile-workflow",
      "description": "Makefile workflow skill for consistent build, lint, test, and check targets across projects",
      "version": "1.1.0",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/makefile-workflow",
      "category": "development"
    },
    {
      "name": "ci-workflow",
      "description": "GitHub Actions CI workflow generation that mirrors local Makefile targets for consistent local and CI behavior",
      "version": "1.0.0",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/ci-workflow",
      "category": "development"
    },
    {
      "name": "pre-commit-lint",
      "description": "Pre-commit hook that runs linters before Claude Code commits changes",
      "version": "1.0.0",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/pre-commit-lint",
      "category": "development"
    },
    {
      "name": "apply-figma-make",
      "description": "Apply Figma Make designs to your website pages with framework conversion",
      "version": "1.0.0",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/apply-figma-make",
      "category": "development"
    },
    {
      "name": "semgrep-review",
      "description": "Analyze semgrep findings, fix real issues, and suppress false positives with proper nosemgrep comments",
      "version": "1.0.1",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/semgrep-review",
      "category": "security"
    },
    {
      "name": "go-dev",
      "description": "Go development skill with idiomatic patterns, testing, and build system detection",
      "version": "1.0.0",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/go-dev",
      "category": "development"
    },
    {
      "name": "gabyx-githooks-setup",
      "description": "Set up shared Git hooks using gabyx/Githooks manager in any repository",
      "version": "1.4.1",
      "author": {
        "name": "Jaehyun Yeom"
      },
      "source": "./plugins/gabyx-githooks-setup",
      "category": "development"
    }
  ]
}
