{
  "name": "john-claude-skills",
  "owner": {
    "name": "John Damask",
    "email": "jbdamask@gmail.com"
  },
  "metadata": {
    "description": "A collection of Claude Code skills for development workflows",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "john-skills",
      "description": "Development workflow tools: skills for devlog, gitingest, mcp-scanner, venv-manager, and more; plus agents like session-recap for context recovery",
      "source": "./",
      "strict": false,
      "agents": ["./agents/session-recap.md"]
    },
    {
      "name": "context-analyzer",
      "description": "Analyze Claude Code chat history to identify context window bloat sources and get optimization recommendations. Parses JSONL session files and reports on image usage, file re-reads, tool output volume, and more.",
      "source": "./plugins/context-analyzer",
      "strict": true
    }
  ]
}
