{
  "name": "jens-marketplace",
  "owner": {
    "name": "Jens Astrup"
  },
  "description": "A collection of plugins for Jens' projects",
  "repository": "https://github.com/jensastrup/jens-marketplace",
  "homepage": "https://github.com/jensastrup/jens-marketplace",
  "plugins": [
    {
      "name": "lint-changes",
      "source": "./plugins/lint-changes-plugin",
      "description": "Run linter on all staged and unstaged changes and fix all warnings/errors",
      "version": "1.0.1",
      "author": {
        "name": "Jens Astrup"
      },
      "keywords": [
        "linting",
        "code-quality",
        "eslint",
        "auto-fix",
        "code-style",
        "formatting",
        "static-analysis"
      ]
    },
    {
      "name": "write-tests",
      "source": "./plugins/write-tests-plugin",
      "description": "Write or update tests for staged/unstaged changes or branch changes",
      "version": "2.2.0",
      "author": {
        "name": "Jens Astrup"
      },
      "keywords": [
        "testing",
        "test-coverage",
        "test-automation",
        "unit-tests"
      ]
    },
    {
      "name": "github",
      "source": "./plugins/github-plugin",
      "description": "Resolve PR review comments by implementing requested changes",
      "version": "1.0.0",
      "author": {
        "name": "Jens Astrup"
      },
      "keywords": [
        "github",
        "pull-request",
        "code-review",
        "pr-comments",
        "review-feedback"
      ]
    },
    {
      "name": "codebase-research",
      "source": "./plugins/codebase-research-plugin",
      "description": "Specialized agents for exploring and understanding codebases - analyze implementations, locate files, and find patterns",
      "version": "1.0.0",
      "author": {
        "name": "Jens Astrup"
      },
      "keywords": [
        "codebase",
        "research",
        "analysis",
        "exploration",
        "patterns",
        "code-navigation"
      ]
    }
  ],
  "strict": false
}
